    public static List<Integer> findSubstring(String s, String[] words) {

         List<Integer> result=new ArrayList<>();

         int n=words[0].length();
         int ncount=words.length;

         HashMap<String,Integer> ori=new HashMap<>();
         for(String word:words){
             ori.put(word,ori.getOrDefault(word,0)+1);
         }

         for(int i=0;i<n;i++){
             HashMap<String,Integer> map=new HashMap<>();
             int index=i;
             int count=0;

             while (index+n<=s.length() && count<ncount){
                 String key=s.substring(index,index+n);
                 map.put(key,map.getOrDefault(key,0)+1);
                 count++;
                 index+=n;
             }

             if(compare(ori,map)){
                 result.add(index-(n*ncount));
             }

             for(int j=index+n;j<=s.length();j=j+n){
                 String key=s.substring(j-n,j);
                 map.put(key,map.getOrDefault(key,0)+1);
                 String oldKey=s.substring(j-(n*(ncount+1)),j-(n*(ncount)));
                 map.put(oldKey,map.get(oldKey)-1);
                 if(compare(ori,map)){
                     result.add(j-(n*(ncount)));
                 }
             }
         }

         return result;
    }

    public static boolean compare(HashMap<String,Integer> map1,HashMap<String,Integer> map2){

        for(Map.Entry<String,Integer> entry:map1.entrySet()){

            if((int)map2.getOrDefault(entry.getKey(),0)!=entry.getValue()){
                return false;
            }
        }

        return true;

    }











